package com.example.Quora.controllers;import com.example.Quora.models.Question;import com.example.Quora.services.QuestionService;import com.example.Quora.services.UserService;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.Optional;@RestController@RequestMapping("/api/questions")public class QuestionController {    private final QuestionService questionService;    public QuestionController(QuestionService questionService) {        this.questionService= questionService;    }    @PostMapping("/")    public Question postQuestion(@RequestBody Question question){        return questionService.postQuestion(question);    }    @GetMapping("/:id")    public Optional<Question> getQuestion(@PathVariable Long id){        return questionService.getQuestion(id);    }    @GetMapping("/all")    public List<Question> getAllQuestion(){        return questionService.getAllQuestion();    }}