package com.example.Quora.controllers;import com.example.Quora.models.User;import com.example.Quora.services.UserService;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController@RequestMapping("/api/user")public class UserController {    private final UserService userService;    public UserController(UserService userService) {        this.userService = userService;    }    @PostMapping("/register")    public User register(@RequestBody  User user){        return userService.register(user);    }    @PostMapping("/login")    public User login(@RequestBody  User user){        return userService.login(user);    }    @GetMapping("/:id")    public Optional<User> Register(@PathVariable  Long id){        return userService.findById(id);    }    @PutMapping("/:id")    public User updateUser(@PathVariable  Long id,@RequestBody  User user){        return userService.updateUser(id,user);    }}