package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.List;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Answer {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false)    private String answer;    @ManyToOne    private  User user;    @ManyToOne    private  Question question;    @OneToMany(mappedBy = "answer")    private List<Comment> comments;    @Override    public String toString() {        return "Answer{" +                "id=" + id +                ", answer='" + answer + '\'' +                ", user=" + user +                ", question=" + question +                ", comments=" + comments +                ", likes=" + likes +                '}';    }    @OneToMany(mappedBy ="answer")    private List<Like> likes;}