package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.List;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Comment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false)    private String content;    @OneToMany(mappedBy = "comment")    private String comment;    @Override    public String toString() {        return "Comment{" +                "id=" + id +                ", content='" + content + '\'' +                ", comment='" + comment + '\'' +                ", answer=" + answer +                ", question=" + question +                '}';    }    @ManyToOne    private List<Answer> answer;    @ManyToOne    private  Question question;}