package com.example.Quora.services;import com.example.Quora.models.Question;import com.example.Quora.repository.QuestionRepository;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class QuestionService {    private  final QuestionRepository questionRepository;    public QuestionService(QuestionRepository questionRepository) {        this.questionRepository = questionRepository;    }    public Question postQuestion(Question question){            return questionRepository.save(question);    }    public Optional<Question> getQuestion(Long id){        return questionRepository.findById(id);    }    public List<Question> getAllQuestion(){        return questionRepository.findAll();    }}